mapper.delete:
DELETE FROM applications
WHERE app_key = #{appkey}
  AND app_id IN

      #{appId}

mapper.select:
SELECT 
FROM applications
WHERE app_key = #{appkey} AND app_id = #{appId}

SELECT 
FROM applications
WHERE app_key = #{appkey}
ORDER BY app_order DESC
LIMIT #{limit}


SELECT 
FROM applications
WHERE app_key = #{appkey}
ORDER BY app_order ASC
LIMIT #{size} OFFSET #{offset}
