mapper.delete:
DELETE FROM blocks
WHERE app_key = #{appkey}
  AND user_id = #{userId}
  AND block_user_id = #{blockUserId}

DELETE FROM blocks
WHERE app_key = #{appkey}
  AND user_id = #{userId}
  AND block_user_id IN

      #{blockUserId}

mapper.select:
SELECT 
FROM blocks b
LEFT JOIN users u
  ON b.app_key = u.app_key AND b.block_user_id = u.user_id
WHERE b.app_key = #{appkey}
  AND b.user_id = #{userId}
  AND b.block_user_id = #{blockUserId}

SELECT b.id, b.user_id, NULL AS nickname, NULL AS user_portrait, NULL AS user_type, NULL AS pinyin, b.block_user_id, UNIX_TIMESTAMP(b.created_time) * 1000 AS created_time_ms, b.app_key FROM blocks b WHERE b.app_key = #{appkey} AND b.user_id = #{userId} AND b.block_user_id IN
#{blockUserId}
ORDER BY b.id ASC

SELECT 
FROM blocks b
LEFT JOIN users u
  ON b.app_key = u.app_key AND b.block_user_id = u.user_id
WHERE b.app_key = #{appkey}
  AND b.user_id = #{userId}
  AND b.id > #{startId}
ORDER BY b.id ASC
LIMIT #{limit}
