mapper.select:
SELECT * FROM users 
WHERE app_key=#{appkey} 
AND user_id IN 

    #{userId}

SELECT * FROM users
WHERE app_key=#{appkey}
AND user_id != #{userId}
AND id > #{startId}
AND (
    user_id LIKE CONCAT('%', #{keyword}, '%')
    OR phone LIKE CONCAT('%', #{keyword}, '%')
    OR email LIKE CONCAT('%', #{keyword}, '%')
    OR nickname LIKE CONCAT('%', #{keyword}, '%')
    OR login_account LIKE CONCAT('%', #{keyword}, '%')
)
ORDER BY id ASC
LIMIT #{limit}

mapper.update:
UPDATE users

    nickname=#{nickname},
    user_portrait=#{userPortrait}

WHERE app_key = #{appkey} AND user_id = #{userId}
